const FinderBase = require('./finder_base');
const FinderAction = require('./finder_action');
const FinderFilter = require('./finder_filter');

/**
 * @extends {FinderBase}
 * @extends {FinderAction}
 * @extends {FinderFilter}
 */
class Finder extends FinderBase {
  constructor(page, ...args){
    super(page, ...args)
  }
  /**
   * Set "selected" property true on option element whose textContent match the given string.
   * This method is assumed to be called on a select element
   * @return {Promise}
   */
  selectOption(content){
    return this.find("option", FinderFilter.haveContent(content.trim())).select();
  }
  /**
   * Alias of "selectOption"
   * @return {Promise}
   */
  choose(content){
    return this.selectOption(content);
  }
  attachFile(filepath){
    // TODO
  }
  /**
   * execute "JavaScript code" given as argument
   * @return {Promise}
   */
  executeJs(code){
    return this.withAction(code).evaluate();
  }
  /**
   * @return {Promise} Promise which resolved with array of finder, that array length is matched number
   */
  toArray(){
    return this.count()
    .then((count)=>{
      let array = [];
      for(let i = 0; i < count; i++){
        array.push(this.clone().find(`(node, idx)=>{ return idx == ${i} }`));
      }
      return array;
    })
  }
  /**
   * @return {Promise} Get new Finder for the first matched element. this is same to "indexOf(0)"
   */
  getFirst(){
    return this.indexOf(0);
  }
  /**
   * @return {Promise} Get new Finder for the given index of matched elements
   */
  indexOf(i){
    return this.count()
    .then((count)=>{
      if(count < i){ throw `Finder have matched less Elements than given value(${i}) !` }
      return this.clone().find(`(node, idx)=>{ return idx == ${i} }`);
    })
  }
  /**
   * return true if all elements which is found by Finder satisfied given filter
   * @example
   * await page.find("input").every(FinderFilter.haveValue("123")) // return true or false
   */
  every(code){
    return this.withMatcher(code, `(filtered)=>{ return filtered.length == elements.length }`)
  }

  clickAndExpectPageLoaded(...args){
    if(args.length > 0){ this.find(...args) }
    return this.page.expectPageLoaded(()=>{ this.click() });
  }
  /**
   * alias of clickAndExpectPageLoaded
   */
  clickAndWaitLoading(...args){
    return this.clickAndExpectPageLoaded(...args);
  }
  clickAndExpectAjaxComplete(...args){
    if(args.length > 0){ this.find(...args) }
    return this.page.expectAjaxComplete(()=>{ this.click() });
  }
}

function mixin(dest, source){
  for(let key of Reflect.ownKeys(source)){
    if(key === "constructor") continue;

    let descriptor = Object.getOwnPropertyDescriptor(source, key);
    descriptor.configurable = true;
    descriptor.enumerable   = false;
    if(descriptor.hasOwnProperty("writable")){
      descriptor.writable = true;
    }
    Object.defineProperty(dest, key, descriptor);
  }
}

mixin(Finder.prototype, FinderAction.prototype);
mixin(Finder.prototype, FinderFilter.prototype);

module.exports = Finder;

/**
 * This class provides action methods for Finder.
 * This class is Mixined to "Finder" class
 */
class FinderAction {
  static get count(){
    return (elements)=>{ return elements ? elements.length : 0 }
  }
  static get text(){
    return (elements)=>{ return elements && elements[0].textContent }
  }
  static setText(text){
    return `(elements)=>{ for(let i = 0; i < elements.length; i++){ elements[i].textContent = "${text}" } }`
  }
  static get value(){
    return (elements)=>{ return elements && elements[0].value }
  }
  static setValue(val){
    return `(elements)=>{ for(let i = 0; i < elements.length; i++){ elements[i].value = "${val}" } }`
  }
  static get click(){
    return (elements)=>{
      if(elements && elements[0]){
        let event = document.createEvent('MouseEvent');
        event.initMouseEvent("click", true, true, window, 0, 0, 0, 0, 0, false, false, false, false, 0, null);
        elements[0].dispatchEvent(event);
      }
    }
  }
  static setChecked(bool){
    return `(elements)=>{ for(let i = 0; i < elements.length; i++){ elements[i].checked = ${bool}; } }`
  }
  static setSelected(bool){
    return `(elements)=>{ elements && elements[0].selected = ${bool} }`
  }

  ////// instance methods //////////////////////////////////////////////////////////////////

  /*
   * Get a textContent of element which is found first by Finder.
   * @return {Promise} Promise resolved with element.textContent
   */
  text(){
    return this.withAction(FinderAction.text).evaluate()
  }
  /**
   * Get a value of element which is found first by Finder.
   * @return {Promise} Promise resolved with element.value
   */
  value(){
    return this.withAction(FinderAction.value).evaluate()
  }
  /**
   * Click an element which is found first by Finder
   * @return {Promise} Promise resolved after click event
   */
  click(){
    return this.withAction(FinderAction.click).evaluate()
  }
  /**
   * Get a count of elements which is found by Finder.
   * @return {Promise} Promise resolved with matched number
   */
  count(){
    return this.withAction(FinderAction.count).evaluate()
  }
  /**
   * @return {Promise} return true if count of elements which is found by Finder is over one.
   */
  exist(){
    return this.count().then(count => count >= 1)
  }
  /**
   * @return {Promise} return true if count of elements which is found by Finder is zero.
   */
  notExist(){
    return this.exist().then(exist => !exist)
  }
  /**
   * Set given value to value of all elements which is found by Finder
   * @return {Promise} Promise resolved after setting value
   */
  setValue(val){
    return this.withAction(FinderAction.setValue(val)).evaluate()
  }
  /**
   * Alias of "setValue"
   */
  fillIn(val){
    return this.setValue(val)
  }
  /**
   * Set given text to textContent of all elements which is found by Finder
   * @return {Promise} Promise resolved after setting textContent
   */
  setText(text){
    return this.withAction(FinderAction.setText(text)).evaluate()
  }
  /**
   * Set "checked" property true on all elements which is found by Finder
   * @return {Promise}
   */
  check(){
    // TODO target is all elements
    return this.withAction(FinderAction.setChecked(true)).evaluate()
  }
  /**
   * Set "checked" property true on all elements which is found by Finder
   * @return {Promise}
   */
  uncheck(){
    // TODO target is all elements
    return this.withAction(FinderAction.setChecked(false)).evaluate()
  }
  /**
   * Alias of "click"
   * @return {Promise}
   */
  chooseRadioButton(){
    return this.click();
  }
  /**
   * Set "selected" property true on element which is found by Finder
   * @return {Promise}
   */
  select(){
    return this.withAction(FinderAction.setSelected(true)).evaluate();
  }
}

module.exports = FinderAction;

const debug = require("../utils/debug").getLogger("app");

class FinderBase {
  static convertArgumentsToConditions(...args){
    return args.map((arg)=>{
      if(typeof arg == 'function'){
        return new Filter(arg.toString());
      }
      if(typeof arg == 'string'){
        if(arg.match(/\)\s*=>\s*\{/) || arg.trim().match(/^function/)){
          return new Filter(arg);
        }else{
          return new Selector(arg);
        }
      }
      throw `Condition parameter is unexpected value, [${arg}]`;
    })
  }
  constructor(page, ...args){
    this._page = page;
    this._conditions = FinderBase.convertArgumentsToConditions(...args);
    this._allowNotFound = false;
    this._matcher = undefined;
    this._action = undefined;
  }
  get page(){ return this._page }
  get conditions(){ return this._conditions }
  get matcher(){ return this._matcher }
  get action(){ return this._action }
  get allowNotFound(){ return this._allowNotFound }
  allowNotFound(val){ this._allowNotFound = val }
  /**
   * @return {Object} cloned object
   */
  clone(){
    return Object.assign(new this.constructor(this.page), {
      _conditions: this.conditions.concat(),
      allowNotFound: this.allowNotFound,
      _matcher: this.matcher,
      _action: this.action
    });
  }
  /**
   * @return {FinderBase} self which have added conditions
   */
  find(...args){
    this._conditions = this.conditions.concat(FinderBase.convertArgumentsToConditions(...args));
    return this;
  }
  withAction(action){
    this._action = typeof action == "function" ?  action.toString() : action;
    return this;
  }
  withMatcher(filter, matcher){
    if(typeof filter == "function"){ filter = filter.toString() }
    if(typeof matcher == "function"){ matcher = matcher.toString() }
    this._matcher = new Matcher(filter, matcher);
    return this;
  }
  /**
   * @return {Promise} Promise resolved in evaluated JavaScript code
   */
  evaluate(){
    if(this.matcher && this.action){ throw "Both action and matcher were specified" }

    let conditions = this.conditions;
    if(!conditions || !(conditions[0] instanceof Selector)){
      conditions = [new Selector("*")];
    }

    let jsCode = `
      var elements = Array.from(document.querySelectorAll("${conditions[0].selector}"));
    `;
    jsCode += conditions.slice(1).map((condition)=>{
      return condition instanceof Selector ?
        `{
          elements = elements.reduce((arr, e, idx)=>{
            return arr.concat(Array.from(e.querySelectorAll("${condition.selector}")));
          }, []);
        }` :
        `{
          let func = ${condition.code};
          elements = elements.filter((e, idx)=>{ return func(e, idx) });
        }`;
    }).join("\n");

    if(!this.allowNotFound){
      jsCode += `
        if(elements.length == 0){
          return Promise.reject('not found');
        }
      `
    }
    if(this.action){
      jsCode += `
        return (${this.action})(elements);
      `;
    }
    else if(this.matcher){
      jsCode += `{
        let filtered = (${this.matcher.filter})(elements);
        return (${this.matcher.matcher})(filtered);
      }`;
    }
    else{
      jsCode += `return elements.length;`
    }
    debug(`Evaluate Javascript "${jsCode}"`)

    return this.page.executeJs(`(()=>{ try{ ${jsCode} }catch(e){ return Promise.reject(e.toString()) } })()`)
    .then((val)=>{
      if(this.matcher && val === false){ throw "No Match!" }
      return val;
    })
    .catch((err)=>{
      debug(`Error occurred in Evaluating Javascript: ${err}`);
      throw err
    })
  }
}

class Filter {
  constructor(code){
    this.code = code;
  }
}
class Selector {
  constructor(selector){
    this.selector = selector;
  }
}
class Matcher {
  constructor(filter, matcher){
    this.filter = filter;
    this.matcher = matcher;
  }
}

module.exports = FinderBase;

class Private {
  static haveContent(content, haveContent = true){
    return `(elm, i)=>{ return elm.textContent && (elm.textContent.trim() == "${content.trim()}") === ${haveContent} }`;
  }
  static haveValue(value, haveValue = true){
    return `(elm, i)=>{ return elm.value && (elm.value.trim() == "${value.trim()}") === ${haveValue} }`;
  }
  static haveClass(classname, haveClass = true){
    return `(elm, i)=>{ return elm && (elm.classList.contains("${classname}")) === ${haveClass} }`;
  }
  static haveStyle(styleName, value, haveStyle = true){
    return `(elm, i)=>{ let style = getComputeStyle(elm); return style && (style["${styleName}"] == "${value}") === ${haveStyle} }`
  }
  static be(propName, bool){
    return `(elm, i)=>{ return elm && elm.${propName} === ${bool} }`;
  }
}

/**
 * This class provides filtering methods for Finder.
 * This class is Mixined to "Finder" class
 */
class FinderFilter {
  /**
   * @return {string} return function code to select elements which have given content as element.textContent
   */
  static haveContent(content){
    return Private.haveContent(content, true);
  }
  /**
   * @return {string} return function code to select elements which do not have given content as element.textContent
   */
  static notHaveContent(content){
    return Private.haveContent(content, false);
  }
  /**
   * @return {string} return function code to select elements which have given value as element.value
   */
  static haveValue(value){
    return Private.haveValue(value, true);
  }
  /**
   * @return {string} return function code to select elements which do not have given value as element.value
   */
  static notHaveValue(value){
    return Private.haveValue(value, false);
  }
  /**
   * @return {string} return function code to select elements which has given value as element.checked attribute.
   */
  static beChecked(bool){
    return Private.be("checked", bool)
  }
  /**
   * @return {string} return function code to select elements which has given value as element.selected attribute.
   */
  static beSelected(bool){
    return Private.be("selected", bool)
  }
  /**
   * @return {string} return function code to select elements which has given value as element.disabled attribute.
   */
  static beDisabled(bool){
    return Private.be("disabled", bool)
  }
  /**
   * @return {string} return function code to select elements which has given value in element.classList attribute.
   */
  static haveClass(classname){
    return Private.haveClass(classname, true);
  }
  /**
   * @return {string} return function code to select elements which do not has given value in element.classList attribute.
   */
  static notHaveClass(classname){
    return Private.haveClass(classname, false);
  }
  /**
   * @return {string} return function code to select elements which has given value in window.getComputedStyle(element)
   */
  static haveStyle(styleName, value){
    return Private.haveStyle(styleName, value, true);
  }
  /**
   * @return {string} return function code to select elements which do not has given value in window.getComputedStyle(element)
   */
  static notHaveStyle(styleName, value){
    return Private.haveStyle(styleName, value, false);
  }

  ////// instance methods /////////////////////////////////////////////////////////////////////

  /**
   * @return {Finder} return filtered Finder object which has the specified value
   */
  haveValue(val){
    return this.find(FinderFilter.haveValue(val))
  }
  /**
   * @return {Finder} return filtered Finder object which has the specified text
   */
  haveText(text){
    return this.find(FinderFilter.haveText(text))
  }
  /**
   * @return {Finder} return filtered Finder object which has been checked
   */
  beChecked(){
    return this.find(FinderFilter.beChecked(true))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has been checked
   */
  notBeChecked(){
    return this.find(FinderFilter.beChecked(false))
  }
  /**
   * @return {Finder} return filtered Finder object which has been selected
   */
  beSelected(){
    return this.find(FinderFilter.beSelected(true))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has been selected
   */
  notBeSelected(){
    return this.find(FinderFilter.beSelected(false))
  }
  /**
   * @return {Finder} return filtered Finder object which has specified CssClass
   */
  haveCssClass(classname){
    return this.find(FinderFilter.haveClass(classname))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has specified CssClass
   */
  notHaveCssClass(classname){
    return this.find(FinderFilter.notHaveClass(classname))
  }
  /**
   * @return {Finder} return filtered Finder object which has specified CssProperty
   */
  haveCssProperty(propName, value){
    return this.find(FinderFilter.haveStyle(propName, value))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has specified CssProperty
   */
  notHaveCssProperty(propName, value){
    return this.find(FinderFilter.notHaveStyle(propName, value))
  }
}

module.exports = FinderFilter;

const {ipcMain} = require('electron');
const Finder = require('./finder');

const TIMEOUT_MSEC = 10000;

class Page {
  constructor(window){
    this._window = window;
  }
  /**
   * @return {Window} Window Object which contains this page.
   */
  get window(){ return this._window }
  /**
   * @return {object} WebContents Object
   * @see https://github.com/electron/electron/blob/master/docs/api/web-contents.md
   */
  get webContents(){ return this.window.browserWindow.webContents }
  /**
   * @private
   */
  checkReady(){
    if(!this.window.isReady) throw "PageContent is not ready!, Please call the 'load' method first"
  }
  /**
   * @return {Promise}
   */
  load(url, options = {}){
    let loadURLFunction = ()=>{
      return this.expectPageLoaded(()=>{ if(url) this.webContents.loadURL(url, options) })
    };
    if(this.window.isReady){
      return loadURLFunction()
    }else{
      return new Promise((resolve, reject)=>{
        this.window.on('ready', ()=>{ loadURLFunction().then(resolve).catch(reject) });
      });
    }
  }
  /**
   * Alias of load
   */
  waitLoading(...args){
    return this.load(...args)
  }
  /**
   * @return {string} URL of current page
   */
  get url(){
    return this.webContents.getURL();
  }
  /**
   * @param {function} action - A function representing the operation of loading new page
   * @return {Promise}
   */
  expectPageLoaded(action){
    return new Promise((resolve,reject)=>{
      try{ this.checkReady() }catch(err){ return reject(err) }
      this.webContents.removeAllListeners('did-finish-load');
      this.webContents.removeAllListeners('did-fail-load');
      try{ action && action() }catch(e){ reject(e) }
      let timeoutId = setTimeout(()=>{ reject('timeout') }, TIMEOUT_MSEC);
      this.webContents.once('did-finish-load', (event)=>{
        clearTimeout(timeoutId)
        resolve(this)
      })
      this.webContents.once('did-fail-load', (event)=>{
        clearTimeout(timeoutId)
        reject(event)
      })
    })
  }
  /**
   * @param {function} action - A function representing the operation of file download
   * @return {Promise}
   */
  expectDownloaded(action){
    return new Promise((resolve,reject)=>{
      try{ this.checkReady() }catch(err){ return reject(err) }

      // TODO
    })
  }
  /**
   * @param {function} action - A function representing the operation of sending IPC
   * @param {string} channel - string of representing the channel of IPC
   * @return {Promise}
   */
  expectIpcReceived(action, channel = 'asynchronous-message'){
    return new Promise((resolve, reject)=>{
      try{ this.checkReady() }catch(err){ return reject(err) }
      try{
        let timeoutId = setTimeout(()=>{ reject('timeout') }, TIMEOUT_MSEC);
        ipcMain.once(channel, (event, arg)=>{
          clearTimeout(timeoutId);
          resolve(arg);
        });
        action && action()
      }catch(e){ reject(e) }
    })
  }
  /**
   * @param {function} action - A function representing the operation of sending Ajax request
   * @return {Promise}
   */
  expectAjaxComplete(action){
    return this.expectIpcReceived(()=>{
      return this.executeJs(`
        if(!XMLHttpRequest.prototype.__original_open){
          XMLHttpRequest.prototype.__original_open = XMLHttpRequest.prototype.open;
        }
        XMLHttpRequest.prototype.open = function(){
          let timeoutId = setTimeout(()=>{ ipcRenderer.send('asynchronous-message', 'timeout') }, ${TIMEOUT_MSEC})
          this.addEventListener('readystatechange', function(){
            if(this.readyState == 4){
              clearTimeout(timeoutId);
              setTimeout(()=>{ ipcRenderer.send('asynchronous-message', 'ajaxComplete') }, 0);
            }
          });
          XMLHttpRequest.prototype.__original_open.apply(this, arguments);
        };
      `)
      .then(()=>{ action && action() })
    })
    .then((result)=>{
      if(result == 'ajaxComplete') return;
      throw result;
    });
  }
  /**
   * @param {string} code - JavaScript Code for executing this page.
   * @return {Promise}
   */
  executeJs(code){
    try{ this.checkReady() }catch(err){ return Promise.reject(err) }
    return this.webContents.executeJavaScript(code, true)
  }
  /**
   * @override
   * @return {Finder} new Finder object
   */
  find(...args){
    return new Finder(this, ...args);
  }
  /**
   * Get the HTML source of current page.
   * @return {Promise} resolved by current HTML source.
   */
  getSourceHTML(){
    return this.executeJs(`document.getElementsByTagName("html")[0].outerHTML`);
  }

  findClickable(){
    return this.find("a,button,input[type=button],input[type=submit]")
  }
}

module.exports = Page;

const EventEmitter = require('events');
const path = require('path');
const {BrowserWindow} = require('electron');
const {config} = require('../utils/config');
const Page = require('./page');

class Window extends EventEmitter {
  constructor(options = {}){
    super();
    let configCloned = config.clone();
    configCloned.merge({ browserWindow: options });
    this.on('ready', ()=>{
      this._browserWindow = new BrowserWindow(configCloned.browserWindow);
      this._browserWindow.webContents.on('context-menu', (e, params)=>{
        this._browserWindow.webContents.inspectElement(params.x, params.y);
      })
    })
  }
  /**
   * @return {object} BrowserWindow Object
   * @see https://github.com/electron/electron/blob/master/docs/api/browser-window.md
   */
  get browserWindow(){ return this._browserWindow }
  /**
   * @private
   */
  get isReady(){ return !!this._browserWindow }
  get page(){
    return this._page = this._page || new Page(this)
  }
}

module.exports = Window;

const electron = require('electron');
const Config = require('./utils/config');
const LoadWalkerModule = ()=>{ return require('./walker/walker') }; // for Lazy loading
const LoadWindowModudle = ()=>{ return require('./page/window') }; // for Lazy loading

class PageWalker {
  constructor(app){
    this.windows = {};
    this._app = app;
    this._app.on('window-all-closed', ()=>{ this._app.quit() });
    this._app.on('ready', ()=>{
      Object.values(this.windows).forEach((window)=>{ window.emit('ready') });
    })
  }
  start(commandLineArgs){
    this._config = Config.create(commandLineArgs);

    const Walker = LoadWalkerModule();
    this._walker = new Walker(Config.config);

    this._app.on('ready', ()=>{
      this._walker.walk((failures)=>{
        console.log(`[${failures == 0? 'SUCCESS': 'FAILURE'}!] finished with status:${failures}`);
        if(this.config.silentMode){
          this._app.exit(failures);  // exit with non-zero status if there were failures
        }else{
          Object.values(this.windows).forEach((window)=>{ window.browserWindow.webContents.openDevTools() });;
        }
      })
    })
  }
  get config(){
    return this._config;
  }
  get page(){
    return this.getWindow().page;
  }
  getWindow(name = "default"){
    if(name == "default" && !this.windows[name]){
      this.createWindow("default", { title: "MainWindow - PageWalker" });
    }
    return this.windows[name]
  }
  createWindow(name, windowOptions = {}){
    const Window = LoadWindowModudle();
    this.windows[name] = new Window(windowOptions);
    if(this._app.isReady()){
      this.windows[name].emit('ready');
    }
  }
}

module.exports = new PageWalker(electron.app)

const fs = require("fs");
const path = require("path");
const OptParser = require("./opt_parser");

/**
 * this class have configuratin information.
 */
class Config {
  static get CONFIG_FILE_PATH(){
    return "config.json";
  }
  static get DEFAULT_CONFIG(){
    return {
      scenarioDir: "scenario",
      scenarioFile: undefined,
      ignoreDir: "supports",
      successSaveFile: "success-scenario.log",
      silentMode: false,
      startFromFirst: false,
      mocha: {
        ui: "bdd",
        bail: true,
        reporter: "spec",
        timeout: 5000,
        slow: false,
      },
      browserWindow: {
        width: 1024,
        height: 768,
        show: true,
        webPreferences: {
          nodeIntegration: false,
          preload: path.join(__dirname, "..", "window_preloaded.js"),
          allowRunningInsecureContent: true
        }
      }
    }
  }
  static create(commandLineArgs){
    this.instance = new Config();
    this.instance.merge(Config.DEFAULT_CONFIG)
    this.instance.loadConfigFile(this.CONFIG_FILE_PATH);
    this.instance.loadConfigFile(this.CONFIG_FILE_PATH + ".local");

    let optParser = new OptParser(commandLineArgs);
    this.instance.merge(optParser.parse((config, cmdOpt)=>{
      switch(cmdOpt.option()){
        case "-r":
        case "--reset":           config.startFromFirst = true; break;
        case "-s":
        case "--silent":          config.silentMode     = true; break;
        default:
          if(this.instance.isExists(cmdOpt.options(true))){
            Config.assign(config, cmdOpt.options(true), cmdOpt.getValueOrFail())
          }else{
            throw new Error("Unexpected CommandLine Argument, [" + cmdOpt.options() + "]");
          }
      }
    }));

    optParser.scriptArguments().forEach((args)=>{
      let filePath = args[0] == "/" ? args : path.join(process.cwd(), args);
      let stat = fs.statSync(filePath)
      let keyName = stat.isDirectory() ? "scenarioDir" : "scenarioFile";
      if(!(this.instance[keyName] instanceof Array)){
        this.instance[keyName] = []
      }
      this.instance[keyName].push(args);
    })

    // apply options
    if(this.instance.silentMode){
      this.instance.browserWindow.show = false;
      this.instance.mocha.reporter = "dot";
    }
    return this.instance;
  }
  static get config(){
    return this.instance;
  }
  /**
   * @private
   */
  loadConfigFile(filePath){
    if(fs.existsSync(filePath)){
      this.merge(JSON.parse(fs.readFileSync(filePath, 'utf8')));
    }
  }
  clone(){
    let cloned = new Config();
    cloned.merge(this);
    return cloned;
  }
  merge(source){
    Config.merge(this, source);
  }
  static merge(dest, source){
    Object.keys(source).forEach((key)=>{
      if(typeof source[key] == "object" && typeof dest[key] == "object"){
        this.merge(dest[key], source[key]);
      }else{
        dest[key] = source[key];
      }
    });
  }
  isExists(key){
    return !!key.split(".").reduce((result, k)=>{ return Config.isExists(result, k) })
  }
  static isExists(object, key){
    if(typeof object != "object") return false;
    if(key in object){
      return typeof object[key] == "object" ? object[key] : true;
    }
  }
  static assign(config, key, value){
    let keys = key.split(".");
    keys.reduce((object, key, index)=>{
      if(index < keys.length - 1){
        return object[key];
      }
      switch(typeof object[key]){
        case 'number':  object[key] = new Number(value);
        case 'boolean': object[key] = new Boolean(value);
        default:        object[key] = value;
      }
    }, config);
  }
}

module.exports = Config;

function getEmptyLogger(){ return (msg)=>{ /* ignore */ }; }
function getPrefixedLogger(prefix){ return (msg)=>{ console.log(`[${prefix}] ${msg}`) }; }

function Debug(){
  console.log(...arguments);
}

Debug.getLogger = (name)=>{
  if(Debug.loggers[name]) return Debug.loggers[name];

  if(Debug.target.includes(name)){
    return Debug.loggers[name] = getPrefixedLogger(name);
  }else{
